import { Component } from '@angular/core';

@Component({
  selector: 'app-national-day-banner-section',
  templateUrl: './national-day-banner-section.component.html',
  styleUrls: ['./national-day-banner-section.component.css']
})
export class NationalDayBannerSectionComponent {

}
<div class="banner">
    <div class="container">
        <div class="banner-content">
            <h2>اليوم الوطني</h2>
            <p>
                تأسيس المملكة العربية السعودية كان في يوم 23 سبتمبر 1932م
            </p>
        </div>

    </div>
</div>
