import { Component } from '@angular/core';

@Component({
  selector: 'app-footer',
  templateUrl: './footer.component.html',
  styleUrls: ['./footer.component.css']
})
export class FooterComponent {

}
<footer>
    <div class="footer-content">

            <p>حفظ الله وطننا الغالي في <br>  ذكرى يومه المجيد</p>

    </div>
    <div class="footer-img">

      <img src="../../../../assets/images/footer-img (2).png" alt="" class="bg-img">
      <img src="../../../../assets/images/bottom-footer-img.png" alt="">

    </div>

</footer>
